import { useState, useEffect } from 'react';

import Card from '../UI/Card';
import MealItem from './MealItem/MealItem';
import useHttp from '../../hooks/use-http';

import classes from './AvailableMeals.module.css';


const AvailableMeals = (props) => {
  const [movies, setMovies] = useState([]);

  const { isLoading, error, sendRequest: fetchMovies } = useHttp();

  useEffect(() => {
    const transformMovies = (mealObj) => {
      const loadedMeals = [];
  
      for (const movieKey in mealObj) {
        loadedMeals.push({ id: movieKey, text: movieObj[movieKey].text });
      }
  
      setMovies(loadedMovies);
    };

    fetchMeals({ 
      url: 'https://react-https-request-cfd6e-default-rtdb.firebaseio.com/movies.json'
    }, transformMovies);
  }, [fetchMovies]);
  // props from Meals
    return (
        <section className={`${classes.meals}`}>
            <Card>
                <ul>
                    {movies.map((meal) => (
                        <MealItem
                            key={meal.id}
                            id={meal.id}
                            name={meal.name}
                            description={meal.description}
                            price={meal.price}
                        />
                    ))}
                </ul>
            </Card>
        </section>
    );
};

export default AvailableMeals;
