import Card from '../UI/Card';
import MealItem from './MealItem/MealItem';
import useHttp from '../../hooks/use-http';

import classes from './AvailableMeals.module.css';


const AvailableMeals = (props) => {
  const [movies, setMovies] = useState([]);

  const { isLoading, error, sendRequest: fetchTasks } = useHttp();

  useEffect(() => {
    const transformMovies = (movieObj) => {
      const loadedMovies = [];
  
      for (const movieKey in movieObj) {
        loadedMOvies.push({ id: movieKey, text: movieObj[movieKey].text });
      }
  
      setTasks(loadedMovies);
    };

    fetchTasks({ 
      url: 'https://react-https-request-cfd6e-default-rtdb.firebaseio.com/movies.json'
    }, transformTasks);
  }, [fetchTasks]);
  // props from Meals
    return (
        <section className={`${classes.meals}`}>
            <Card>
                <ul>
                    {movies.map((meal) => (
                        <MealItem
                            key={meal.id}
                            id={meal.id}
                            name={meal.name}
                            description={meal.description}
                            price={meal.price}
                        />
                    ))}
                </ul>
            </Card>
        </section>
    );
};

export default AvailableMeals;
