import {legacy_createStore as createStore} from 'redux';

const counterReducer = (state={counter: 0}, action) => {
    switch (action.type) {
        case 'increment':
            return {
                counter: state.counter + 1
            };
        case 'increase':
            return {
                counter: state.counter + action.value
            }
        case 'decrement':
            return {
                counter: state.counter - 1
            }
        default:
            return state;
    }
}

export const store = createStore(counterReducer);
